(()=>{"use strict";window.addEventListener("DOMContentLoaded",(function(){new class{constructor(){const e=Array.prototype.slice.call(document.getElementsByClassName("modal__button"),0),t=Array.prototype.slice.call(document.querySelectorAll(".modal__background"),0);e.forEach((e=>{e.addEventListener("click",(()=>{const t=e.dataset.target;document.querySelector("#"+t).classList.add("is-show")}))})),t.forEach((e=>{e.addEventListener("click",(()=>{document.querySelector(".modal.is-show").classList.remove("is-show")}))}))}}}))})();